import React, { useEffect } from "react";
import ISidebarKeyValues from "../interfaces/ISidebarKeyValues";
import './Sidebar.scss';

type SidebarProps = {
  title: string,
  titleLink: string,
  sidebarItems: ISidebarKeyValues[]
}

const Sidebar = ({ title, titleLink, sidebarItems }: SidebarProps) => {

  let items: Array<object> = [];

  const iterateItems = () => {
    items.push(
      <div>
        {
          sidebarItems.map((item) => {
            return (
              <a href={item.url}>{item.name}</a>
            )
          }
          )
        }
      </div>
    )
    return items;
  }

  useEffect(() => {
    iterateItems();
  })

  return (
    <>
      <div className="sidebar">
        <div className="sidebar-logo">
          <a href={titleLink}>{title}</a>
        </div>
        <div className="sidebar-item">
          {items}
        </div>
      </div>
    </>
  )
}

export default Sidebar;
