import React from "react";
import ISidebarKeyValues from "../interfaces/ISidebarKeyValues";
import './Sidebar.scss';

type SidebarProps = {
  title: string,
  titleLink: string,
  sidebarItems: ISidebarKeyValues[]
}

const Sidebar = ({ title, titleLink, sidebarItems }: SidebarProps) => {

  let items: Array<object> = [];

  const iterateItems = () => {
    for (let i: number = 0; i < sidebarItems.length; i += 1) {
      items.push(
        <>
          {
            sidebarItems.slice(i).map(
              (item) => {
                return(
                  <a href={item.url}>{item.name}</a>
                )
              }
            )
          }
        </>
      )
    }
    return items;
  }

  return (
    <>
      <div className="sidebar">
        <div className="sidebar-logo">
          <a href={titleLink}>{title}</a>
        </div>
        <div className="sidebar-item">
          { iterateItems() }
        </div>
      </div>
    </>
  )
}

export default Sidebar;
